import React from 'react'
import './Styles/App.css'
import {useForm} from 'react-hook-form'
import {Card,Button} from 'react-bootstrap'
import {Link,useHistory} from 'react-router-dom'
import axios from 'axios'
import minion from './Images/minion.png'
const Login = (props) => {
  const {handleSubmit,register} = useForm();
  const[ err,seterr] = React.useState(0);
  const history = useHistory();
  const onSubmit = async(data) => {
    const value= 
      {
        "username": data.username,
         "password":data.password  
        }
    console.log(value);
     const res = await axios.post(`http://13.233.129.83:3000/auth/signin`,value) 
    .then(res => {
      console.log(res);
      localStorage.setItem('accesstoken',res.data.accessToken)
      history.push('/generatelink')
    })
    .catch(err => {
      console.log(err);
      seterr(1);
    })
  }
  return (

    <div className="login"> 

    <form onSubmit={handleSubmit(onSubmit)}>
    <Card className="min-card">
  <Card.Img variant="top" src={minion} />
  <Card.Body variant="center">
    <Card.Title>Login</Card.Title>
    {err === 1 ? <p align="left" style={{color:'red'}}>Invalid username or password</p> : null}
   <Link to="/signup"><h6 align="left">Need an account?</h6></Link><br/>
    <Card.Text>
      <label htmlFor="input" name="username">Username</label>
      <input type="text" name="username" className="textbox" required ref={register({required:'Username is required'})}/>
      <label htmlFor="input" name="password">Password</label>
      <input type="password" name="password" className="textbox" required ref={register({required:'Password is required'})}/>
    </Card.Text>
    <Button variant="primary" className="btn" type="submit">Login</Button><br/><br/>
    <Link to="/resetpassword"><h6 align="center">Forgot Password?</h6></Link>
  </Card.Body>
</Card>
     </form>


    </div>
  )
}

export default Login
